pragma solidity >=0.8.0;

contract Companies {
    // Add owner constructor
    struct Company {
        string name;
        string description;
        bool active;
    }
    address private owner;
    uint256 companies_count;

    mapping(address => Company) internal companies;

    constructor() {
        owner = msg.sender;
        // companies_count = 0;
    }

    function addCompany(
        string memory name,
        string memory description,
        address company_adress
    ) external {
        Company memory _company = Company(name, description, true);
        companies[company_adress] = _company;
        companies_count++;
    }

    function removeCompany(address company_adress) external {
        require(owner == msg.sender, "No sufficient right");
        companies[company_adress].active = false;
        companies_count--;
    }

    function companyInfo(
        address company_adress
    ) external view returns (Company memory) {
        return companies[company_adress];
    }

    function companyCount() public view returns (uint256) {
        return companies_count;
    }

    modifier onlyCompany() {
        require(
            companies[msg.sender].active == true,
            "Needs to be valid company"
        );
        _;
    }

    // modifier onlyAllOwner() {
    //     require(owner == msg.sender, "No sufficient right as owner");
    //     _;
    // }

    // function is_admin(address _admin) public view returns (bool) {
    //     if (_admin == owner) return true;
    //     return false;
    // }

    function is_company(address _company) public view returns (bool) {
        if (companies[_company].active) return true;
        return false;
    }
}

contracts/EIP712Base.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

contract EIP712Base {

    struct EIP712Domain {
        string name;
        string version;
        address verifyingContract;
        bytes32 salt;
    }

    bytes32 internal constant EIP712_DOMAIN_TYPEHASH = keccak256(bytes("EIP712Domain(string name,string version,address verifyingContract,bytes32 salt)"));

    bytes32 internal domainSeparator;

    constructor(string memory name, string memory version) {
        domainSeparator = keccak256(abi.encode(
            EIP712_DOMAIN_TYPEHASH,
            keccak256(bytes(name)),
            keccak256(bytes(version)),
            address(this),
            bytes32(getChainID())
        ));
    }

    function getChainID() internal view returns (uint256 id) {
        assembly {
            id := chainid()
        }
    }

    function getDomainSeparator() private view returns(bytes32) {
        return domainSeparator;
    }

    /**
    * Accept message hash and returns hash message in EIP712 compatible form
    * So that it can be used to recover signer from signature signed using EIP712 formatted data
    * https://eips.ethereum.org/EIPS/eip-712
    * "\\x19" makes the encoding deterministic
    * "\\x01" is the version byte to make it compatible to EIP-191
    */
    function toTypedMessageHash(bytes32 messageHash) internal view returns(bytes32) {
        return keccak256(abi.encodePacked("\x19\x01", getDomainSeparator(), messageHash));
    }

}

contracts/EIP712MetaTransaction.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "./EIP712Base.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

contract EIP712MetaTransaction is EIP712Base {
    using SafeMath for uint256;
    bytes32 private constant META_TRANSACTION_TYPEHASH = keccak256(bytes("MetaTransaction(uint256 nonce,address from,bytes functionSignature)"));

    event MetaTransactionExecuted(address userAddress, address payable relayerAddress, bytes functionSignature);
    mapping(address => uint256) private nonces;

    /*
     * Meta transaction structure.
     * No point of including value field here as if user is doing value transfer then he has the funds to pay for gas
     * He should call the desired function directly in that case.
     */
    struct MetaTransaction {
        uint256 nonce;
        address from;
        bytes functionSignature;
    }

    constructor(string memory name, string memory version) public EIP712Base(name, version) {}

    function convertBytesToBytes4(bytes memory inBytes) internal returns (bytes4 outBytes4) {
        if (inBytes.length == 0) {
            return 0x0;
        }

        assembly {
            outBytes4 := mload(add(inBytes, 32))
        }
    }

    function executeMetaTransaction(address userAddress,
        bytes memory functionSignature, bytes32 sigR, bytes32 sigS, uint8 sigV) public payable returns(bytes memory) {
        bytes4 destinationFunctionSig = convertBytesToBytes4(functionSignature);
        require(destinationFunctionSig != msg.sig, "functionSignature can not be of executeMetaTransaction method");
        MetaTransaction memory metaTx = MetaTransaction({
            nonce: nonces[userAddress],
            from: userAddress,
            functionSignature: functionSignature
        });
        require(verify(userAddress, metaTx, sigR, sigS, sigV), "Signer and signature do not match");
        nonces[userAddress] = nonces[userAddress].add(1);
        // Append userAddress at the end to extract it from calling context
        (bool success, bytes memory returnData) = address(this).call(abi.encodePacked(functionSignature, userAddress));

        require(success, "Function call not successful");
        emit MetaTransactionExecuted(userAddress, payable(msg.sender), functionSignature);
        return returnData;
    }

    function hashMetaTransaction(MetaTransaction memory metaTx) internal pure returns (bytes32) {
        return keccak256(abi.encode(
            META_TRANSACTION_TYPEHASH,
            metaTx.nonce,
            metaTx.from,
            keccak256(metaTx.functionSignature)
        ));
    }

    function getNonce(address user) external view returns(uint256 nonce) {
        nonce = nonces[user];
    }

    function verify(address user, MetaTransaction memory metaTx, bytes32 sigR, bytes32 sigS, uint8 sigV) internal view returns (bool) {
        address signer = ecrecover(toTypedMessageHash(hashMetaTransaction(metaTx)), sigV, sigR, sigS);
        require(signer != address(0), "Invalid signature");
        return signer == user;
    }

    function msgSender() internal view returns(address sender) {
        if(msg.sender == address(this)) {
            bytes memory array = msg.data;
            uint256 index = msg.data.length;
            assembly {
                // Load the 32 bytes word from memory with the address on the lower 20 bytes, and mask those.
                sender := and(mload(add(array, index)), 0xffffffffffffffffffffffffffffffffffffffff)
            }
        } else {
            sender = msg.sender;
        }
        return sender;
    }
}

contracts/Institutes.sol.content:
pragma solidity >=0.8.0;

contract Institutes {
    // Add owner constructor
    struct Institute {
        string name;
        string description;
        bool active;
    }
    address private owner;
    uint256 institutes_count;
    // uint256 active_institutes_count;

    mapping(address => Institute) internal institutes;
    address[] internal institutes_list;

    constructor() {
        owner = msg.sender;
        // institutes_count = 0;
    }

    function addInstitute(
        string memory name,
        string memory description,
        address institute_adress
    ) external {
        require(owner == msg.sender, "No sufficient right");
        Institute memory _institute = Institute(name, description, true);
        institutes[institute_adress] = _institute;
        institutes_list.push(institute_adress);
        institutes_count++;
    }

    function removeInstitute(address institute_adress) external {
        require(owner == msg.sender, "No sufficient right");
        institutes[institute_adress].active = false;
        institutes_count--;
    }

    function instituteInfo(
        address institute_adress
    ) external view returns (Institute memory) {
        return institutes[institute_adress];
    }

    function instituteList() external view returns (address[] memory) {
        return institutes_list;
    }

    function instituteCount() public view returns (uint256) {
        return institutes_count;
    }

    modifier onlyInstitute() {
        require(
            institutes[msg.sender].active == true,
            "Needs to be valid institute"
        );
        _;
    }

    modifier onlyAllOwner() {
        require(owner == msg.sender, "No sufficient right as owner");
        _;
    }

    function is_admin(address _admin) public view returns (bool) {
        if (_admin == owner) return true;
        return false;
    }

    function is_institute(address _institute) public view returns (bool) {
        if (institutes[_institute].active) return true;
        return false;
    }
}

contracts/SbtConvertible.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/utils/Base64.sol";
import "./Institutes.sol";
import "./Companies.sol";

import {Functions, FunctionsClient} from "@chainlink/contracts/src/v0.8/dev/functions/FunctionsClient.sol"; // Once published
import {ConfirmedOwner} from "@chainlink/contracts/src/v0.8/ConfirmedOwner.sol";

contract SbtConvertible is
    ERC721URIStorage,
    Institutes,
    Companies,
    FunctionsClient,
    ConfirmedOwner
{
    address private _owner;
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;
    using Functions for Functions.Request;

    event SBT_Burnt(uint256 tokenId, string tokenURI);

    bytes32 public latestRequestId;
    bytes public latestResponse;
    bytes public latestError;

    uint32 public gasLimit = 250000;
    uint64 public subscriptionId = 0;
    string source =
        string(
            abi.encodePacked(
                "let jsonObject = JSON.parse(args[0]);",
                "const principalAmount = parseInt(jsonObject.carbon_credits);",
                "return Functions.encodeUint256(Math.round(principalAmount));"
            )
        );

    function setGasLimit(uint32 _gasLimit) public onlyAllOwner returns (bool) {
        gasLimit = _gasLimit;
        return true;
    }

    function setSubscriptionId(
        uint64 _subscriptionId
    ) public onlyAllOwner returns (bool) {
        subscriptionId = _subscriptionId;
        return true;
    }

    event OCRResponse(bytes32 indexed requestId, bytes result, bytes err);

    constructor(
        address oracle
    )
        ERC721("Convertible Carbon-Credits SBT", "CCCSBT")
        FunctionsClient(oracle)
        ConfirmedOwner(msg.sender)
    {}

    function executeRequest(
        string memory source,
        bytes memory secrets,
        string[] memory args,
        uint64 subscriptionId,
        uint32 gasLimit
    ) public onlyOwner returns (bytes32) {
        Functions.Request memory req;
        req.initializeRequest(
            Functions.Location.Inline,
            Functions.CodeLanguage.JavaScript,
            source
        );
        if (secrets.length > 0) {
            req.addRemoteSecrets(secrets);
        }
        if (args.length > 0) req.addArgs(args);

        bytes32 assignedReqID = sendRequest(req, subscriptionId, gasLimit);
        latestRequestId = assignedReqID;
        return assignedReqID;
    }

    /**
     * @notice Callback that is invoked once the DON has resolved the request or hit an error
     *
     * @param requestId The request ID, returned by sendRequest()
     * @param response Aggregated response from the user code
     * @param err Aggregated error from the user code or from the execution pipeline
     * Either response or error parameter will be set, but never both
     */
    function fulfillRequest(
        bytes32 requestId,
        bytes memory response,
        bytes memory err
    ) internal override {
        latestResponse = response;
        latestError = err;
        emit OCRResponse(requestId, response, err);
    }

    /**
     * @notice Allows the Functions oracle address to be updated
     *
     * @param oracle New oracle address
     */
    function updateOracleAddress(address oracle) public onlyOwner {
        setOracle(oracle);
    }

    function addSimulatedRequestId(
        address oracleAddress,
        bytes32 requestId
    ) public onlyOwner {
        addExternalRequest(oracleAddress, requestId);
    }

    struct SBT {
        string institute_name; // address of institute
        string company_name; //can be enum
        string carbon_credits;
        string issue_date;
        string industry_id;
